// Keyboard accessible custom dropdown

function AccessibleDropdown({ options, value, onChange }) {
  const [isOpen, setIsOpen] = useState(false);
  const [highlightedIndex, setHighlightedIndex] = useState(0);
  const buttonRef = useRef();

  const handleKeyDown = (e) => {
    switch (e.key) {
      case 'Enter':
      case ' ':  // Space
        e.preventDefault();
        if (isOpen) {
          onChange(options[highlightedIndex]);
          setIsOpen(false);
        } else {
          setIsOpen(true);
        }
        break;
      case 'Escape':
        setIsOpen(false);
        buttonRef.current?.focus();
        break;
      case 'ArrowDown':
        e.preventDefault();
        if (!isOpen) {
          setIsOpen(true);
        } else {
          setHighlightedIndex(i => 
            i < options.length - 1 ? i + 1 : i
          );
        }
        break;
      case 'ArrowUp':
        e.preventDefault();
        setHighlightedIndex(i => i > 0 ? i - 1 : i);
        break;
    }
  };

  return (
    <div className="dropdown">
      <button
        ref={buttonRef}
        onClick={() => setIsOpen(!isOpen)}
        onKeyDown={handleKeyDown}
        aria-haspopup="listbox"
        aria-expanded={isOpen}
      >
        {value || 'Select an option'}
      </button>

      {isOpen && (
        <ul role="listbox" aria-activedescendant={`option-${highlightedIndex}`}>
          {options.map((option, index) => (
            <li
              key={option}
              id={`option-${index}`}
              role="option"
              aria-selected={index === highlightedIndex}
              className={index === highlightedIndex ? 'highlighted' : ''}
              onClick={() => {
                onChange(option);
                setIsOpen(false);
                buttonRef.current?.focus();
              }}
            >
              {option}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

// Usage
function App() {
  const [selected, setSelected] = useState('');

  return (
    <AccessibleDropdown
      options={['Apple', 'Banana', 'Cherry']}
      value={selected}
      onChange={setSelected}
    />
  );
}

// Screen reader friendly components

// Accessible image gallery
function ImageGallery({ images }) {
  return (
    <ul className="gallery">
      {images.map(img => (
        <li key={img.id}>
          <figure>
            <img
              src={img.src}
              alt={img.alt}
              loading="lazy"
            />
            <figcaption>{img.caption}</figcaption>
          </figure>
        </li>
      ))}
    </ul>
  );
}

// Accessible alert/notification
function Alert({ type, message, onClose }) {
  return (
    <div
      role="alert"
      aria-live="assertive"
      className={`alert alert-${type}`}
    >
      <span className="alert-icon" aria-hidden="true">
        {type === 'error' && '⚠️'}
        {type === 'success' && '✓'}
      </span>
      <span className="alert-message">{message}</span>
      <button
        onClick={onClose}
        aria-label="Dismiss alert"
      >
        ×
      </button>
    </div>
  );
}

// Accessible data table
function UserTable({ users }) {
  return (
    <table>
      <caption>Registered Users</caption>
      <thead>
        <tr>
          <th scope="col">Name</th>
          <th scope="col">Email</th>
          <th scope="col">Role</th>
          <th scope="col">Actions</th>
        </tr>
      </thead>
      <tbody>
        {users.map(user => (
          <tr key={user.id}>
            <td>{user.name}</td>
            <td>{user.email}</td>
            <td>{user.role}</td>
            <td>
              <button
                aria-label={`Edit ${user.name}`}
                onClick={() => editUser(user)}
              >
                Edit
              </button>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
}

// CSS to visually hide but keep accessible
const srOnly = {
  position: 'absolute',
  width: '1px',
  height: '1px',
  padding: '0',
  margin: '-1px',
  overflow: 'hidden',
  clip: 'rect(0, 0, 0, 0)',
  whiteSpace: 'nowrap',
  border: '0'
};
