exercises.solution:
function Product({ name, price, inStock }) {
  return (
    <div>
      <h3>{name}</h3>
      <p>${price}</p>
      <span style={{ color: inStock ? 'green' : 'red' }}>
        {inStock ? 'In Stock' : 'Out of Stock'}
      </span>
    </div>
  );
}

