// Working with JSON in JavaScript

// Parsing JSON from API
const response = await fetch('/api/users/1');
const data = await response.json();
console.log(data);
// {"id": 1, "name": "Alice", "email": "alice@example.com"}

// Creating JSON
const user = {
  name: "Bob",
  age: 30,
  hobbies: ["coding", "gaming"],
  address: {
    city: "Boston",
    state: "MA"
  }
};

const jsonString = JSON.stringify(user, null, 2);
console.log(jsonString);
/*
{
  "name": "Bob",
  "age": 30,
  "hobbies": [
    "coding",
    "gaming"
  ],
  "address": {
    "city": "Boston",
    "state": "MA"
  }
}
*/

// Error handling
try {
  const obj = JSON.parse('{invalid json}');
} catch (error) {
  console.error('Invalid JSON:', error.message);
}

// Reading local JSON file (Node.js)
const data = require('./data.json');

// Or with ES modules
import data from './data.json' assert { type: 'json' };

// Logging in Node.js with Winston

const winston = require('winston');

// Configure logger
const logger = winston.createLogger({
  level: process.env.LOG_LEVEL || 'info',
  format: winston.format.combine(
    winston.format.timestamp(),
    winston.format.json()
  ),
  transports: [
    new winston.transports.File({ filename: 'error.log', level: 'error' }),
    new winston.transports.File({ filename: 'combined.log' })
  ]
});

// Add console logging in development
if (process.env.NODE_ENV !== 'production') {
  logger.add(new winston.transports.Console({
    format: winston.format.simple()
  }));
}

// Usage
logger.info('Server started', { port: 3000 });

logger.warn('API response slow', {
  endpoint: '/api/users',
  duration: 3500,
  threshold: 2000
});

logger.error('Database query failed', {
  query: 'SELECT * FROM users',
  error: err.message,
  stack: err.stack
});

// In Express middleware
app.use((req, res, next) => {
  const start = Date.now();

  res.on('finish', () => {
    logger.info('HTTP Request', {
      method: req.method,
      url: req.url,
      status: res.statusCode,
      duration: Date.now() - start,
      userId: req.user?.id
    });
  });

  next();
});

// Error handling
app.use((err, req, res, next) => {
  logger.error('Unhandled error', {
    error: err.message,
    stack: err.stack,
    url: req.url,
    method: req.method,
    userId: req.user?.id
  });

  res.status(500).json({ error: 'Internal server error' });
});

// Environment variables in practice

// 1. Install dotenv
// npm install dotenv

// 2. Create .env file
/*
DB_HOST=localhost
DB_PORT=5432
DB_NAME=myapp
DB_USER=admin
DB_PASSWORD=secret123

STRIPE_SECRET_KEY=sk_test_...
STRIPE_PUBLISHABLE_KEY=pk_test_...

JWT_SECRET=my-super-secret-key
JWT_EXPIRES_IN=7d

NODE_ENV=development
PORT=3000
*/

// 3. Load environment variables
require('dotenv').config();

// 4. Use in your app
const express = require('express');
const stripe = require('stripe')(process.env.STRIPE_SECRET_KEY);

const app = express();

// Database config
const dbConfig = {
  host: process.env.DB_HOST,
  port: process.env.DB_PORT,
  database: process.env.DB_NAME,
  user: process.env.DB_USER,
  password: process.env.DB_PASSWORD
};

// Environment-specific behavior
if (process.env.NODE_ENV === 'production') {
  app.use(someProductionMiddleware());
} else {
  app.use(developmentLogger());
}

// Validation helper
function requireEnvVars(...vars) {
  const missing = vars.filter(v => !process.env[v]);

  if (missing.length > 0) {
    throw new Error(`Missing required env vars: ${missing.join(', ')}`);
  }
}

requireEnvVars('DB_HOST', 'DB_PASSWORD', 'JWT_SECRET');

// Start server
const port = process.env.PORT || 3000;
app.listen(port, () => {
  console.log(`Server running on port ${port}`);
  console.log(`Environment: ${process.env.NODE_ENV}`);
});

