// React (Library) - You control everything

import React, { useState } from 'react';
import ReactDOM from 'react-dom/client';
// You choose your router
import { BrowserRouter } from 'react-router-dom';
// You choose your state management
import { Provider } from 'react-redux';

function Counter() {
  const [count, setCount] = useState(0);

  return (
    <div>
      <p>Count: {count}</p>
      <button onClick={() => setCount(count + 1)}>
        Increment
      </button>
    </div>
  );
}

// You compose everything yourself
const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <BrowserRouter>
    <Provider store={store}>
      <Counter />
    </Provider>
  </BrowserRouter>
);

// â€•â€•â€•â€•â€•â€•â€•â€•â€•â€•â€•â€•â€•â€•â€•â€•â€•â€•â€•â€•â€•â€•â€•â€•â€•â€•â€•â€•â€•â€•â€•â€•â€•â€•â€•â€•

// Angular (Framework) - It controls the flow

import { Component } from '@angular/core';
import { Router } from '@angular/router';

// Must use decorators
@Component({
  selector: 'app-counter',
  template: `
    <div>
      <p>Count: {{ count }}</p>
      <button (click)="increment()">Increment</button>
    </div>
  `
})
export class CounterComponent {
  count = 0;

  // Angular calls this
  constructor(private router: Router) {}

  // Angular calls this
  ngOnInit() {
    console.log('Component initialized');
  }

  increment() {
    this.count++;
  }
}

// Angular bootstraps and manages everything
// You just define components following its patterns

