// Complete React example: User dashboard

import { useState, useEffect } from 'react';

// Custom hook for data fetching
function useUser(userId) {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setLoading(true);
    fetch(`/api/users/${userId}`)
      .then(r => r.json())
      .then(data => {
        setUser(data);
        setLoading(false);
      });
  }, [userId]);

  return { user, loading };
}

// Reusable components
function Avatar({ url, name }) {
  return (
    <img 
      src={url} 
      alt={name}
      className="w-16 h-16 rounded-full"
    />
  );
}

function UserCard({ user }) {
  return (
    <div className="bg-white p-6 rounded-lg shadow">
      <Avatar url={user.avatar} name={user.name} />
      <h2 className="text-xl font-bold mt-4">{user.name}</h2>
      <p className="text-gray-600">{user.email}</p>
      <div className="mt-4">
        <span className="text-sm">Posts: {user.postCount}</span>
      </div>
    </div>
  );
}

// Main component
function UserDashboard() {
  const [selectedUserId, setSelectedUserId] = useState(1);
  const { user, loading } = useUser(selectedUserId);

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-bold mb-6">User Dashboard</h1>

      <div className="mb-4">
        <button 
          onClick={() => setSelectedUserId(1)}
          className="mr-2 px-4 py-2 bg-blue-500 text-white rounded"
        >
          User 1
        </button>
        <button 
          onClick={() => setSelectedUserId(2)}
          className="px-4 py-2 bg-blue-500 text-white rounded"
        >
          User 2
        </button>
      </div>

      <UserCard user={user} />
    </div>
  );
}

export default UserDashboard;

// Vue 3 Composition API example: Todo app

<template>
  <div class="todo-app">
    <h1>My Todos</h1>

    <form @submit.prevent="addTodo">
      <input 
        v-model="newTodo"
        placeholder="What needs to be done?"
        class="todo-input"
      />
      <button type="submit">Add</button>
    </form>

    <ul class="todo-list">
      <li 
        v-for="todo in todos" 
        :key="todo.id"
        :class="{ completed: todo.done }"
      >
        <input 
          type="checkbox" 
          v-model="todo.done"
        />
        <span>{{ todo.text }}</span>
        <button @click="removeTodo(todo.id)">Delete</button>
      </li>
    </ul>

    <div class="stats">
      <p>Total: {{ totalTodos }}</p>
      <p>Completed: {{ completedTodos }}</p>
      <p>Remaining: {{ remainingTodos }}</p>
    </div>
  </div>
</template>

<script setup>
import { ref, computed } from 'vue';

// State
const newTodo = ref('');
const todos = ref([
  { id: 1, text: 'Learn Vue', done: true },
  { id: 2, text: 'Build an app', done: false }
]);

// Computed properties
const totalTodos = computed(() => todos.value.length);
const completedTodos = computed(() => 
  todos.value.filter(t => t.done).length
);
const remainingTodos = computed(() => 
  totalTodos.value - completedTodos.value
);

// Methods
function addTodo() {
  if (newTodo.value.trim()) {
    todos.value.push({
      id: Date.now(),
      text: newTodo.value,
      done: false
    });
    newTodo.value = '';
  }
}

function removeTodo(id) {
  todos.value = todos.value.filter(t => t.id !== id);
}
</script>

<style scoped>
.todo-app {
  max-width: 600px;
  margin: 0 auto;
  padding: 20px;
}

.completed {
  text-decoration: line-through;
  opacity: 0.6;
}
</style>

// Complete Svelte example: User list with filtering

<script>
  import { onMount } from 'svelte';

  let users = [];
  let loading = true;
  let searchTerm = '';

  // Reactive filtered list
  $: filteredUsers = users.filter(user => 
    user.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // Reactive count
  $: displayCount = filteredUsers.length;

  onMount(async () => {
    const response = await fetch('/api/users');
    users = await response.json();
    loading = false;
  });

  async function deleteUser(id) {
    await fetch(`/api/users/${id}`, { method: 'DELETE' });
    users = users.filter(u => u.id !== id);
  }
</script>

<style>
  .user-card {
    border: 1px solid #ddd;
    padding: 1rem;
    margin: 0.5rem 0;
    border-radius: 4px;
  }

  .user-card:hover {
    background: #f5f5f5;
  }
</style>

<div class="container">
  <h1>User Directory</h1>

  <input 
    type="text"
    bind:value={searchTerm}
    placeholder="Search users..."
    class="search-input"
  />

  <p>Showing {displayCount} users</p>

  {#if loading}
    <p>Loading...</p>
  {:else if filteredUsers.length === 0}
    <p>No users found</p>
  {:else}
    {#each filteredUsers as user (user.id)}
      <div class="user-card">
        <h3>{user.name}</h3>
        <p>{user.email}</p>
        <button on:click={() => deleteUser(user.id)}>
          Delete
        </button>
      </div>
    {/each}
  {/if}
</div>

<!-- Notice how clean and simple this is compared to React/Vue! -->

// Angular example: User list component

// user.service.ts
import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

interface User {
  id: number;
  name: string;
  email: string;
}

@Injectable({ providedIn: 'root' })
export class UserService {
  private apiUrl = '/api/users';

  constructor(private http: HttpClient) {}

  getUsers(): Observable<User[]> {
    return this.http.get<User[]>(this.apiUrl);
  }

  deleteUser(id: number): Observable<void> {
    return this.http.delete<void>(`${this.apiUrl}/${id}`);
  }
}

// user-list.component.ts
import { Component, OnInit } from '@angular/core';
import { UserService } from './user.service';

@Component({
  selector: 'app-user-list',
  template: `
    <div class="container">
      <h1>Users</h1>

      <input 
        [(ngModel)]="searchTerm"
        placeholder="Search users..."
        class="search-input"
      />

      <div *ngIf="loading">Loading...</div>

      <div *ngIf="!loading" class="user-list">
        <div 
          *ngFor="let user of filteredUsers" 
          class="user-card"
        >
          <h3>{{ user.name }}</h3>
          <p>{{ user.email }}</p>
          <button (click)="deleteUser(user.id)">
            Delete
          </button>
        </div>
      </div>
    </div>
  `,
  styles: [`
    .user-card {
      border: 1px solid #ddd;
      padding: 1rem;
      margin: 0.5rem 0;
    }
  `]
})
export class UserListComponent implements OnInit {
  users: User[] = [];
  loading = true;
  searchTerm = '';

  get filteredUsers() {
    return this.users.filter(user => 
      user.name.toLowerCase().includes(this.searchTerm.toLowerCase())
    );
  }

  constructor(private userService: UserService) {}

  ngOnInit() {
    this.userService.getUsers().subscribe(
      data => {
        this.users = data;
        this.loading = false;
      },
      error => console.error('Error:', error)
    );
  }

  deleteUser(id: number) {
    this.userService.deleteUser(id).subscribe(
      () => {
        this.users = this.users.filter(u => u.id !== id);
      }
    );
  }
}

