// Real-world styled-components example

import styled from 'styled-components';

// Theming
const theme = {
  colors: {
    primary: '#007bff',
    danger: '#dc3545',
    gray: '#6c757d'
  },
  spacing: (multiplier) => `${8 * multiplier}px`
};

// Base button
const Button = styled.button`
  padding: ${props => props.theme.spacing(1.5)} ${props => props.theme.spacing(3)};
  border: none;
  border-radius: 4px;
  font-weight: bold;
  cursor: pointer;
  transition: all 0.3s;

  background: ${props => {
    if (props.variant === 'primary') return props.theme.colors.primary;
    if (props.variant === 'danger') return props.theme.colors.danger;
    return props.theme.colors.gray;
  }};

  color: white;

  &:hover {
    opacity: 0.8;
    transform: translateY(-2px);
  }

  &:disabled {
    opacity: 0.5;
    cursor: not-allowed;
  }
`;

// Extended variant
const PrimaryButton = styled(Button)`
  background: linear-gradient(45deg, #007bff, #0056b3);
`;

// Usage
function App() {
  return (
    <ThemeProvider theme={theme}>
      <div>
        <Button variant="primary">Primary</Button>
        <Button variant="danger">Delete</Button>
        <PrimaryButton>Fancy Primary</PrimaryButton>
      </div>
    </ThemeProvider>
  );
}

