// This is what happens when you visit a website:

// 1. Client makes a request
fetch('https://api.example.com/users')
  .then(response => response.json())
  .then(data => {
    console.log('Received from server:', data);
  });

// 2. Server responds with data
// Server code (Node.js example):
app.get('/users', (req, res) => {
  res.json({ users: ['Alice', 'Bob'] });
});

// Working with URLs in JavaScript

const url = new URL('https://example.com/search?q=react&page=2');

console.log(url.protocol);  // 'https:'
console.log(url.hostname);  // 'example.com'
console.log(url.pathname);  // '/search'
console.log(url.search);    // '?q=react&page=2'

// Get specific query parameter
const params = new URLSearchParams(url.search);
console.log(params.get('q'));     // 'react'
console.log(params.get('page'));  // '2'

// Build a URL programmatically
const apiUrl = new URL('https://api.example.com/users');
apiUrl.searchParams.set('limit', '10');
apiUrl.searchParams.set('sort', 'name');
console.log(apiUrl.toString());
// 'https://api.example.com/users?limit=10&sort=name'

// Making HTTP requests in JavaScript

// GET request
fetch('https://api.example.com/users/1')
  .then(response => {
    console.log('Status:', response.status); // 200
    return response.json();
  })
  .then(data => console.log(data));

// POST request
fetch('https://api.example.com/users', {
  method: 'POST',
  headers: {
    'Content-Type': 'application/json',
    'Authorization': 'Bearer token123'
  },
  body: JSON.stringify({
    name: 'Alice',
    email: 'alice@example.com'
  })
})
  .then(response => response.json())
  .then(data => console.log('Created:', data));

// Error handling
fetch('https://api.example.com/users/999')
  .then(response => {
    if (response.status === 404) {
      console.log('User not found!');
    }
    if (!response.ok) {
      throw new Error('Request failed');
    }
    return response.json();
  })
  .catch(error => console.error('Error:', error));

// Using a REST API in JavaScript

const API_URL = 'https://api.example.com';
const API_KEY = 'your-api-key';

// Helper function
async function apiRequest(endpoint, options = {}) {
  const response = await fetch(`${API_URL}${endpoint}`, {
    ...options,
    headers: {
      'Content-Type': 'application/json',
      'X-API-Key': API_KEY,
      ...options.headers
    }
  });

  if (!response.ok) {
    throw new Error(`API Error: ${response.status}`);
  }

  return response.json();
}

// GET - Fetch all users
const users = await apiRequest('/users');

// GET - Fetch specific user
const user = await apiRequest('/users/123');

// POST - Create new user
const newUser = await apiRequest('/users', {
  method: 'POST',
  body: JSON.stringify({
    name: 'Alice',
    email: 'alice@example.com'
  })
});

// PUT - Update user
const updated = await apiRequest('/users/123', {
  method: 'PUT',
  body: JSON.stringify({ name: 'Alice Smith' })
});

// DELETE - Remove user
await apiRequest('/users/123', { method: 'DELETE' });

// DNS resolution happens automatically, but here's what's happening:

// When you do this:
fetch('https://api.example.com/data')

// Behind the scenes:
// 1. Browser: "What's the IP for api.example.com?"
// 2. DNS: "It's 93.184.216.34"
// 3. Browser connects to: https://93.184.216.34/data
//    (with Host header: api.example.com)

// You can see DNS in action with Node.js:
const dns = require('dns');

dns.resolve4('google.com', (err, addresses) => {
  if (err) throw err;
  console.log('IP addresses:', addresses);
  // Output: ['172.217.14.206', ...]
});

// Or check all record types:
dns.resolve('example.com', 'ANY', (err, records) => {
  console.log(records);
  // Shows A, MX, TXT, etc.
});

// In the browser, you can't directly access DNS,
