function getTotalRevenue(orders) {
  return orders
    .filter(order => order.status = 'completed')
    .map(order => order.total)
    .reduce((sum, total) => sum + total);
}

exercises.solution:
function getTotalRevenue(orders) {
  return orders
    .filter(order => order.status === 'completed')
    .map(order => order.total)
    .reduce((sum, total) => sum + total, 0);
}
