lessons.codeExample:
// const for things that don't change
const PI = 3.14159;
const greeting = "Hello World";

// let for things that do change
let count = 0;
count += 1;

// Template literals use backticks
const message = `Count is: ${count}`;
console.log(message);

const name = "Alice";
const age = 25;
const isStudent = true;
const hobbies = ["coding", "reading"];
const address = null;

console.log(typeof name);      // "string"
console.log(typeof age);       // "number"
console.log(typeof isStudent); // "boolean"
console.log(typeof hobbies);   // "object"
console.log(typeof address);   // "object" (known JS quirk!)

const a = 10;
const b = 3;

console.log(a + b);  // 13
console.log(a % b);  // 1 (remainder)
console.log(a ** b); // 1000 (power)

console.log(a === 10);  // true (strict equality)
console.log(a !== b);   // true

const isAdult = a >= 18;
const canDrive = isAdult && true;

