exercises.solution:
function UserProfile() {
  const name = "Alice";
  const age = 25;
  let score = 0;
  score += 10;

  return (
    <div>
      <h2>{name}</h2>
      <p>Age: {age}</p>
      <p>Score: {score}</p>
    </div>
  );
}
