  // Return user profile display

}

exercises.solution:
interface UserProfileProps {
  name: string;
  age: number;
  isAdmin?: boolean;
}

function UserProfile({ name, age, isAdmin }: UserProfileProps) {
  return (
    <div>
      <h2>{name} {isAdmin && '(Admin)'}</h2>
      <p>Age: {age}</p>
    </div>
  );
}

function formatPrice(amount: number): string {
  return "$" + amount.toFixed(2);
}

